  public FrameScreen(FrameMain parent, String title, int w, int h, int base_address, int size, int color_address)
   {
	super(title);
    
    this.title         = title;
    this.parent        = parent;
    this.base_address  = base_address;
    this.color_address = color_address;
    this.size          = size;
    initColors();
    initAscii();

    setDefaultCloseOperation(JFrame.HIDE_ON_CLOSE);
    setLayout(null);
    setResizable(false);

    // Init screen
    image  = new BufferedImage(128, 96, BufferedImage.TYPE_INT_ARGB);
    screen = new JLabel(new ImageIcon(image));
    screen.setBounds(96,16,128,96);
    add(screen);
    
    // Add buttons and labels with info
    label1 = parent.createLabel("Video control address bits (#" + color_address + "):", 4,127,316,40,parent.font_mono); add(label1);
    label2 = parent.createLabel("M = 1 (8x3 characters)",                               4,147,316,40,parent.font_mono); add(label2);
    label3 = parent.createLabel("M = 0 (16x12 pixels)",                                 4,167,316,40,parent.font_mono); add(label3);
    label4 = parent.createLabel("[U][M][FGC][BGC]",                                    10,187,316,40,parent.font_mono); add(label4);
    label5 = parent.createLabel("[0][0][000][000]",                                    10,207,316,40,parent.font_mono); add(label5);
    clear  = new JButton("Clear"); clear.setBounds(70, 243, 180, 30); clear.setFont(parent.font);                       add(clear);

    // Add backgroung
    JLabel bg = new JLabel(new ImageIcon(FrameMain.DATA_PATH + "screen.png"));
    bg.setBounds(0, 0, w, h);
    add(bg);
    
    setVisible(true);
    setSize(w + getInsets().left + getInsets().right, h + getInsets().top + getInsets().bottom);
    setLocationRelativeTo(parent);
    setAlwaysOnTop(true);
    setVisible(false);
    
    // Events
    clear.addActionListener (e -> { clearPressed(); });
    
    // Start thread
    refresh_thread = new Thread() { @Override public void run() { while(true) { FrameMain.sleep(10); Refresh(); } } };
    refresh_thread.start();
   }

   // Buttons events  
   public void clearPressed() { for(int i = 0; i < size; i++) parent.ram.Write(i + base_address, ""); } // Clear Video RAM

   // Refresh thread
   private void Refresh()
    {
	 int v    = parent.ram.ReadInt(color_address); if(v<0 || v>255) v = 0;
	 int mode = FrameMain.getBit(v,6); // 0X000000 Extract bit 6  [0-1]
	 int bg   = v  & 7;                // 00000xxx background color index [0-7]
	 int fg   = (v & (7 << 3)) >> 3;   // 00xxx000 foreground color index [0-7]
	 //System.out.println(String.format("%8s", Integer.toBinaryString(v)).replace(' ', '0') + " v:" + v + " m:" + mode + " bg:" + bg + " fg:" + fg);
	 label5.setText("[" + FrameMain.getBit(v,7) + "][" + FrameMain.getBit(v,6) + "][" + FrameMain.getBit(v,5)+FrameMain.getBit(v,4)+FrameMain.getBit(v,3) + "][" + FrameMain.getBit(v,2)+FrameMain.getBit(v,1)+FrameMain.getBit(v,0) + "]"); 
	 clear(image,COLORS[bg]);
     if(mode == 1) drawTextMode(COLORS[fg]); else drawGfxMode(COLORS[fg]);
	 screen.repaint();
	}

  private void drawTextMode(int color)
   {
	// Resolution: 8x3 (1 char = 16x32 pixel in real display) // 128 chars (first bit = reverse)
	for(int i = 0; i < size; i++)
     {
	  char pix = '1';
	  int v = parent.ram.ReadInt(i + base_address); if(v<0 || v>255) v = 0; if(v>127) pix = '0'; v%=128;
	  int x = (i%8) * 16;
	  int y = (i/8) * 32;
	  for(int p = 0; p < ASCII[v].length(); p++)
	   {
		if(ASCII[v].charAt(p) == pix) image.setRGB(x+(p%16), y+(p/16), color);
	   }
	 }
   }

  private void drawGfxMode(int color)
   {
    // Resolution: 16x12 (1 pixel = 8x8 pixel in real display)
	for(int i = 0; i < size; i++)
     {
	  int v = parent.ram.ReadInt(i + base_address);
	  for(int b = 0; b < 8; b++)
	   {
	    int x  = (i%2 + (7-b)) + (i%2 * 7);
	    int y  = i / 2;
	    int p  = ((1 << b) & v) >> b;
	    if(p == 1) putPixel(image,x,y,color);
	   }
     }
   }

   private void clear(BufferedImage img, int color)
    {
	 for(int y = 0; y < img.getHeight(); y++)
	  for(int x = 0; x < img.getWidth(); x++)
       img.setRGB(x, y, color);
    }

   private void putPixel(BufferedImage img, int x, int y, int color)
    {
	 int s = 8;
	 for(int xx = x*s; xx < (x*s) + s; xx++)
 	  for(int yy = y*s; yy < (y*s) + s; yy++)
       img.setRGB(xx, yy, color);
    }

  private void initColors()
   {
	COLORS = new int[8];  // MS DOS COLORS: https://lospec.com/palette-list/16-colors-dos-ansi-art-palette-rejected-2hak
	COLORS[0] = Color.BLACK.getRGB();
	COLORS[1] = Color.WHITE.getRGB();
	COLORS[2] = Color.GRAY.getRGB();
	COLORS[3] = Color.RED.getRGB();
	COLORS[4] = Color.BLUE.getRGB();
	COLORS[5] = Color.GREEN.getRGB();
	COLORS[6] = Color.YELLOW.getRGB();
	COLORS[7] = Color.ORANGE.getRGB();
   }

